$(window).scroll(function() {
    let $sections = $('._anchor-target');
    $sections.each(function(i, el) {
        let top = $(el).offset().top * .98;
        let bottom = top + $(el).height();
        let scroll = $(window).scrollTop();
        let idn = $(el).attr('id');
        if (scroll > top && scroll < bottom) {
            $('.about-nav a').removeClass('_active');
            $('.about-nav a[href="#' + idn + '"]').addClass('_active');
        }
    });
});

